s = ''''''


for i in range(0,len(s)-3):
    f = False
    o = s[i:i+4]
    for j in range(0,4):
        for k in range(j+1,4):
            if o[j] == o[k]:
                f = True
                break
    if f == True:
        continue
    else:
        print(i+4)
        break    

